export default {
  props: {
    loading: Boolean,
    noData: Boolean,
    offset: Number

  },
  name: "index",
  mounted() {
    //绑定事件监听，滚动的时候触发
    window.addEventListener('scroll', this.handleScroll, false);
  },
  beforeDestroy() {
    //移出事件监听
    window.removeEventListener('scroll', this.handleScroll)
  },
  data() {
    return {
      scrollAction: {
        x: 'undefined',
        y: 'undefined'
      }
    }
  },
  methods: {
    handleScroll(e) {
      var that = this
      //
      if (!that.noData) {
        //如果有数据，触发
        let curHeight = document.documentElement.scrollTop || document.body.scrollTop
        //获取div区域
        var scrollPage = document.getElementById('scroll-page');

        if ((curHeight + window.innerHeight >= that.$refs.scroll.offsetHeight + that.offset) && that.isDownDirection()) {
          //判断是否到达底部
          if (!that.loading) {
            //调用load加载数据
            that.$emit('load')
          }
        }
      }
    },
    isDownDirection() {
      if (typeof this.scrollAction.x == 'undefined') {
        this.scrollAction.x = window.pageXOffset;
        this.scrollAction.y = window.pageYOffset;
      }
      var diffX = this.scrollAction.x - window.pageXOffset;
      var diffY = this.scrollAction.y - window.pageYOffset;

      this.scrollAction.x = window.pageXOffset;
      this.scrollAction.y = window.pageYOffset;

      if (diffX < 0) {
        // Scroll right
      } else if (diffX > 0) {
        // Scroll left
      } else if (diffY < 0) {
        // Scroll down
        return true
      } else if (diffY > 0) {
        // Scroll up
      } else {
        // First scroll event
      }
      return false
    }

  }
}
