import BaseHeader from "@/components/common/BaseHeader";
import MarkdownEditor from "@/components/markdown/MarkdownEditor";
import {publishArticle, getArticleById} from "@/api/article";
import {getAllcategory} from "@/api/category";
import {getAlltags} from "@/api/tag";


export default {
  name: "BlogWrite",
  components: {
   "markdown-editor": MarkdownEditor,
   "base-header": BaseHeader
  },
  data(){
      return{
        publishVisible: false,
        categorys: [],
        tags:[],
        articleForm:{
                id: '',
                title: '',
                summary: '',
                category: '',
                tags: [],
                editor: {
                  value: '',
                  ref: '',//保存mavonEditor实例  实际不该这样
                  default_open: 'edit',
                  toolbars: {
                    bold: true, // 粗体
                    italic: true, // 斜体
                    header: true, // 标题
                    underline: true, // 下划线
                    strikethrough: true, // 中划线
                    mark: true, // 标记
                    superscript: true, // 上角标
                    subscript: true, // 下角标
                    quote: true, // 引用
                    ol: true, // 有序列表
                    ul: true, // 无序列表
                    imagelink: true, // 图片链接
                    code: true, // code
                    fullscreen: true, // 全屏编辑
                    readmodel: true, // 沉浸式阅读
                    help: true, // 帮助
                    undo: true, // 上一步
                    redo: true, // 下一步
                    trash: true, // 清空
                    navigation: true, // 导航目录
                    //subfield: true, // 单双栏模式
                    preview: true, // 预览
                  }
                }
            },
        rules:{
          summary:[
            {required: true, message: '请输入摘要', trigger: 'blur'},
            {max: 80, message: '不能大于80个字符', trigger: 'blur'}
          ],
          category: [
            {required: true, message: '请选择文章分类', trigger: 'change'}
          ],
          tags: [
            {type: 'array', required: true, message: '请选择标签', trigger: 'change'}
          ]
        }
      }
  },
  computed:{
      title(){
        return '写文章'
    }
  },
  mounted() {
    if(this.$route.params.id){
      this.getArticleById(this.$route.params.id)
    }

    this.getCategorysAndTags()
    this.editorToolBarToFixedWrapper = this.$_.throttle(this.editorToolBarToFixed, 200)
    window.addEventListener('scroll', this.editorToolBarToFixedWrapper, false);
  },
  beforeDestroy() {
    window.removeEventListener('scroll', this.editorToolBarToFixedWrapper, false)
  },
  methods:{
    getArticleById(id){
      alert(111)
      let that=this
      getArticleById(id).then((res)=>{
        Object.assign(that.articleForm,res.data.data)
        that.articleForm.editor.value=res.data.data.body.content
        let tags=this.articleForm.tags.map(function (item){
          return item.id;
        })
        this.articleForm.tags=tags;
      }).catch(error => {
        if (error !== 'error') {
          that.$message({type: 'error', message: '文章加载失败', showClose: true})
        }
      })
    },
    publishShow(){
      if(!this.articleForm.title){
        this.$message({message:'标题不能为空哦',type:'warning',showClose:true})
        return
      }
      if (this.articleForm.title.length > 30) {
        this.$message({message:'标题不能大于30个字符',type:'warning',showClose:true})
        return
      }
      if(!this.articleForm.editor.ref.d_render){
        this.$message({message:'内容不能为空哦',type:'warning',showClose:true})
        return
      }
      this.publishVisible=true;
    },
    publish(articleForm){
      let that=this

      that.$refs[articleForm].validate((valid) => {
        if(valid) {
          let tags=this.articleForm.tags.map(function (item) {
            return {id: item};
          });

          let article={
            id:this.articleForm.id,
            title:this.articleForm.title,
            summary:this.articleForm.summary,
            category:this.articleForm.category,
            tags: tags,
            body:{
              content:this.articleForm.editor.value,
              contentHtml:this.articleForm.editor.ref.d_render
            }
          }
          this.publishVisible=false;
          let loading =this.$loading({
            lock:true,
            text:'正在发布文章，请稍后...'
          })

          publishArticle(article,this.$store.state.token).then((res)=>{
            if(res.data.success){
              loading.close();
              that.$message({message:'文章发布成功！',type:'success',showClose:true})
              that.$router.push({path:`/view/${res.data.data.id}`})
            }else{
              that.$message({message:'发布文章失败:'+res.data.msg,type:'error',showClose:true})
            }
          }).catch((error)=>{
            loading.close();
            if(error!=='error'){
              that.$message({message:'发布文章失败:'+res.data.msg,type:'error',showClose:true})
            }
          })
        }
      });
    },
    cancel(){
        this.$confirm('文章将不会保存，是否继续？',"提示",{
          confirmButtonText:'确定',
          cancelButtonText:'取消',
          type:'warning'
        }).then(()=>{
          this.$router.push('/')
        })
    },

    getCategorysAndTags(){
      let that=this
      getAllcategory().then((res)=>{
        //发起http请求，后端的数据
          if(res.data.success){
            that.categorys = res.data.data
          }else{
            this.$message({type: 'error', message: '文章分类加载失败', showClose: true})
          }
        }).catch((err)=>{
          if (err !== 'error') {
            this.$message({type: 'error', message: '文章分类加载失败', showClose: true})
          }
        })
      getAlltags().then((res)=>{
        //发起http请求，后端的数据
        if(res.data.success){
          that.tags = res.data.data
        }else{
          this.$message({type: 'error', message: '标签加载失败', showClose: true})
        }
      }).catch((err)=>{
        if (err !== 'error') {
          that.$message({type: 'error', message: err.msg, showClose: true})
        }
      })

    },
    editorToolBarToFixed() {
      let toolbar = document.querySelector('.v-note-op');
      let curHeight = document.documentElement.scrollTop || document.body.scrollTop;
      if (curHeight >= 160) {
        document.getElementById("placeholder").style.display = "block"; //bad  用计算属性较好
        toolbar.classList.add("me-write-toolbar-fixed");
      } else {
        document.getElementById("placeholder").style.display = "none";
        toolbar.classList.remove("me-write-toolbar-fixed");
      }
    }
  },
  //组件内的守卫 调整body的背景色
  beforeRouteEnter(to, from, next) {
    window.document.body.style.backgroundColor = '#fff';
    next();
  },
  beforeRouteLeave(to, from, next) {
    window.document.body.style.backgroundColor = '#f5f5f5';
    next();
  }
}
